  overflow-y: scroll;
  background-color: var(--hty-bg-color, #f5f5f5);
}
body::before {
  content: '';
  position: fixed;
  width: 100%;
  height: 100%;
  z-index: -1;
  background-image: url("https://cdn.jsdelivr.net/gh/YunYouJun/cdn/img/bg/stars-timing-0-blur-30px.jpg");
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  animation-name: bgFadeIn;
  animation-duration: 2s;
  opacity: 1;
}
@-moz-keyframes bgFadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@-webkit-keyframes bgFadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@-o-keyframes bgFadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@keyframes bgFadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
:root {
  --banner-line-color: #000;
  --banner-char-color: #000;
}
#banner {
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 100vh;
}
.vertical-line-top,
.vertical-line-bottom {
  display: flex;
  background-color: var(--banner-line-color);
  width: 1px;
  height: 0;
  animation-duration: 0.4s;
  animation-fill-mode: forwards;
  animation-timing-function: ease-in;
}
.vertical-line-bottom {
  position: absolute;
  bottom: 0;
}
.char {
  font-family: 'Songti SC', 'Noto Serif SC', STZhongsong, STKaiti, KaiTi, Roboto, serif;
  font-size: var(--banner-char-size);
  font-weight: 900;
  background-color: rgba(255,255,255,0.5);
  line-height: 1;
  transition-property: all;
  transition-duration: 0.2s;
  transition-delay: 0s;
}
.char:hover {
  color: #fff;
  background-color: var(--banner-char-color);
}
.char-left,
.char-right {
  display: flex;
  color: var(--banner-char-color);
  opacity: 0;
}
.char-left {
  border-left: 1px solid var(--banner-line-color);
  border-right: 0px solid rgba(0,120,231,0.1);
  border-right-width: 0px;
  animation-duration: 0.4s;
  animation-delay: 0.4s;
  animation-fill-mode: forwards;
  animation-timing-function: ease-out;
}
.char-right {
  border-left: 0px solid rgba(0,120,231,0.1);
  border-right: 1px solid var(--banner-line-color);
  border-left-width: 0px;
  animation-duration: 0.4s;
  animation-delay: 0.4s;
  animation-fill-mode: forwards;
  animation-timing-function: ease-out;
}
.cloud {
  display: flex;
  width: 100%;
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: 8;
  box-sizing: border-box;
  mix-blend-mode: overlay;
}
.cloud .waves {
  display: flex;
  position: relative;
  width: 100%;
  height: 100px;
}
@media (max-width: 768px) {
  .cloud .waves {
    height: 40px;
  }
}
.cloud .parallax > use {
  animation: move-forever 25s cubic-bezier(0.55, 0.5, 0.45, 0.5) infinite;
}
.cloud .parallax > use:nth-child(1) {
  animation-delay: -2s;
  animation-duration: 7s;
}
.cloud .parallax > use:nth-child(2) {
  animation-delay: -3s;
  animation-duration: 10s;
}
.cloud .parallax > use:nth-child(3) {
  animation-delay: -4s;
  animation-duration: 13s;
}
.cloud .parallax > use:nth-child(4) {
  animation-delay: -5s;
  animation-duration: 20s;
}
@-moz-keyframes move-forever {
  0% {
    transform: translate3d(-90px, 0, 0);
  }
  100% {
    transform: translate3d(85px, 0, 0);
  }
}
@-webkit-keyframes move-forever {
  0% {
    transform: translate3d(-90px, 0, 0);
  }
  100% {
    transform: translate3d(85px, 0, 0);
