  justify-content: flex-end;
}
.tag,
.category {
  font-size: 0.8rem;
  position: relative;
  color: var(--text-color, #000);
  letter-spacing: 1px;
}
.tag::before,
.category::before {
  content: '';
  position: absolute;
  bottom: 0;
  height: 100%;
  width: 0;
  right: 0;
  background: rgba(0,120,231,0.08);
  border-radius: 4px;
  transition: width 0.2s ease;
}
.tag:hover::before,
.category:hover::before {
  width: 104%;
  left: -2%;
}
.tag .icon,
.category .icon {
  width: 0.8rem;
  height: 0.8rem;
  padding-left: 0.1rem;
}
.post-top-icon {
  position: absolute;
  top: 1rem;
  right: 1rem;
  color: #f2711c;
}
.post-top-icon .icon {
  width: 1.5rem;
  height: 1.5rem;
}
.post-collapse {
  position: relative;
  margin: 0 5rem 2rem 5rem;
}
@media screen and (max-width: 1024px) {
  .post-collapse {
    margin: 0 2rem 1rem 2rem;
  }
}
@media screen and (max-width: 768px) {
  .post-collapse {
    margin: 0 1rem 0.5rem 1rem;
  }
