  text-align: center;
}
.post-collapse .collection-title {
  position: relative;
  margin: 0;
  border-bottom: 2px solid rgba(0,120,231,0.6);
}
.post-collapse .collection-title::before {
  content: '';
  position: absolute;
  top: 50%;
  width: 2px;
  height: 50%;
  background: rgba(0,120,231,0.3);
}
.post-collapse .collection-title .archive-year {
  font-family: 'Source Code Pro', 'Courier New', Courier, Consolas, Monaco, monospace;
  color: #0078e7;
  margin: 0 1.5rem;
}
.post-collapse .collection-title .archive-year::before {
  content: '';
  position: absolute;
  left: 0;
  top: 35%;
  margin-left: -11px;
  margin-top: -4px;
  width: 1.5rem;
  height: 1.5rem;
  background: #0078e7;
  border-radius: 50%;
}
.post-collapse .post-item {
  position: relative;
}
.post-collapse .post-item::before {
  content: '';
  position: absolute;
  width: 2px;
  height: 100%;
  background: rgba(0,120,231,0.3);
}
.post-collapse .post-header {
  position: relative;
  border-bottom: 2px solid rgba(0,120,231,0.3);
  display: flex;
}
.post-collapse .post-header::before {
  content: '';
  position: absolute;
  left: 0;
  top: 1.6rem;
  width: 0.5rem;
  height: 0.5rem;
  margin-left: -4px;
  border-radius: 50%;
  border: 1px solid #0078e7;
  background-color: #fff;
  z-index: 1;
  transition-property: all;
  transition-duration: 0.2s;
  transition-delay: 0s;
  transition-property: background;
}
.post-collapse .post-header:hover::before {
  background: #0078e7;
}
.post-collapse .post-header .post-title {
  margin-left: 0.1rem;
  padding: 0;
  font-size: 1rem;
  display: inline-flex;
  align-items: center;
}
.post-collapse .post-header .post-title .post-title-link .icon {
  width: 1.1rem;
  height: 1.1rem;
  margin-right: 0.3rem;
}
.post-collapse .post-header .post-meta {
  font-family: 'Source Code Pro', 'Courier New', Courier, Consolas, Monaco, monospace;
  font-size: 1rem;
  margin: 1rem 0 1rem 1.2rem;
  white-space: nowrap;
}
.last-word {
